from calendar import monthrange
from datetime import date


def is_end_of_month(d: date) -> bool:
    """
    Check if the given date refers to the last day of a month.

    Not the last day of the month.
    >>> is_end_of_month(date(2020, 12, 15))
    False

    Last day of month.
    >>> is_end_of_month(date(2020, 12, 31))
    True
    """
    (first, last) = monthrange(d.year, d.month)
    return d.day == last


def get_end_of_month(d: date) -> date:
    """
    Get the last day of the month that contains the given date.

    :param d: Any date.

    :return: A date at the end of the month.

    >>> get_end_of_month(date(2016, 11, 12))
    datetime.date(2016, 11, 30)
    """
    (first, last) = monthrange(d.year, d.month)
    return date(d.year, d.month, last)
