class DotDict(dict):
    """
    A dot-able dictionary for easy access to items.

    Note: stay clear from keys that clashes with dict internals like:
    copy, fromkeys, get, items, keys, pop, popitem, setdefault, update, and values.
    ...

    Examples
    --------
    >>> a = DotDict(val1=1)
    >>> a.val2 = 2
    >>> a
    {'val1': 1, 'val2': 2}
    >>> a['val1']
    1
    >>> # noinspection PyUnresolvedReferences
    >>> a.val1
    1
    """
    def __init__(self, **kwds):
        super().__init__()
        self.update(kwds)
        self.__dict__ = self
