import numpy as np


def int_parseable(s):
    """
    Test if str can be parsed to an int

    >>> int_parseable("moo")
    False
    >>> int_parseable(9)
    True
    """
    try:
        int(s)
        return True
    except ValueError:
        return False


def is_nan(x):
    """
    A more extensive nan test than numpy's `isnan`. It returns True for `nan`s as well as `None`s.

    >>> is_nan(5)
    False
    >>> is_nan(None)
    True
    >>> is_nan(np.nan)
    True
    """
    try:
        return np.isnan(x)
    except TypeError:
        if x is None:
            return True
        else:
            return False
