import subprocess


def sh(cmd):
    """
    Get string output from running a terminal command. On error, return None.

    >>> sh(['git', '--version']) #doctest: +ELLIPSIS
    'git version...'

    >>> sh(['asdfasdasdfsdjg']) is None
    True
    """
    try:
        return subprocess.run(
            cmd,
            check=True,
            stdout=subprocess.PIPE,
            stderr=subprocess.DEVNULL,
        ).stdout.decode('utf-8').strip()
    except (subprocess.CalledProcessError, FileNotFoundError):
        return None
