"""
Validators for :class:`aa_py_xl.formula.operators.Function` objects.

Every function in this file must take one argument of type List[FormulaNode], which represents the function arguments,
and must return None. To indicate that the function call is not valid, raise an Error.
"""
from functools import lru_cache
from typing import List

from pydicti import dicti

from aa_py_xl.formula.node import FormulaNode


def test(arguments: List[FormulaNode]) -> None:
    if len(arguments):
        raise ValueError(f"Function '{test.__name__}' takes no arguments.")


@lru_cache(1)
def get_xl_function_validators():
    return dicti({
        'test': test,
    })
