from typing import Iterable, List


def sanitize_columns(columns: Iterable[str]) -> List[str]:
    """
    Make sure 'columns' is a list strings.
    - If they are not strings, we get weird conversion issues like https://github.com/AutoActuary/aa-py-core/issues/59
    - If it's not a List, but some other sequence type like `df.columns`, xlwings hangs indefinitely.

    TODO: what is the proper type hint for any object that implements the `__str__` method?
    """
    return [str(c).strip() for c in columns]
